package com.hedza06.springelastic.controllers;import com.hedza06.springelastic.services.ProductService;import org.springframework.http.HttpStatus;import org.springframework.http.MediaType;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;@RestController@RequestMapping(value = "/api/product", produces = MediaType.APPLICATION_JSON_VALUE)public class ProductController {    private final ProductService productService;    public ProductController(ProductService productService) {        this.productService = productService;    }    @PostMapping    public ResponseEntity<Void> bulkInsert()    {        productService.insertAll();        return new ResponseEntity<>(HttpStatus.NO_CONTENT);    }}